/*global exports*/
import SignStream from '../lib/sign-stream';
import VerifyStream, { verify, decode, isValid } from '../lib/verify-stream';

var ALGORITHMS = [
  'HS256', 'HS384', 'HS512',
  'RS256', 'RS384', 'RS512',
  'PS256', 'PS384', 'PS512',
  'ES256', 'ES384', 'ES512'
];

const _ALGORITHMS = ALGORITHMS;
export { _ALGORITHMS as ALGORITHMS };
export const sign = SignStream.sign;
export const verify = verify;
export const decode = decode;
export const isValid = isValid;
export function createSign(opts) {
  return new SignStream(opts);
}
export function createVerify(opts) {
  return new VerifyStream(opts);
}
